INSERT INTO public."SavedList" (id, language, title, content, "userId", "createdAt") VALUES ('cm51h89160007r4gu2im4olr3', 'Russian', 'Элегия Джека', '{"inputText":"В этом мире никто\nНе сравниться со мной\nПризнан лучшим я в своём ремесле\nУжас я навожу под немою луной\nНа любого из живущих на земле\n\nСтолько видел храбрецов,\nХвастунов, глупцов.\nКолотила их мелкая дрожь\n\nВ бегство я обращал\nИ детей, и отцов.\nЯ как призрак удивительно хорош!\n\nТак за годом в год я пугал народ,\nА в душе печаль,так и жизнь пройдёт\nИ Я ДЖЕК! ПОВЕЛИТЕЛЬ ТЫКВ!\nНенавижу всё, к чему привык\n\nВ моей душе одна печаль\nС тоской гляжу в ночную даль\nНе разорвать порочный круг\nКуда бежать от этих мук?...\n\nЯ известен везде,\nЯ кошмар в темноте\nЯ тот ужас на крыльях ночи\nУхнув из-за плеча,\nЯ пугал англичан\nИ французев, и немцев и прочих\n\nКак и все, кто мертвы,\nДаже без головы\nЯ по памяти цитирую Шекспира\nУ людей, у зверей\nНет оскала страшней\nЯ король потустороннего мира\n\nНо кто здесь поймёт мою печаль?\nПовелителю Тыкв самому себя жаль\nИ корона тяжела,\nДа и слава не мила\nСвой унылый мир я бы сжёг до тла...\n\nЧто светит там, в конце пути?\nОдна тоска в моей груди\nПрестиж, почёт и высший свет\n\nВсё ни о чём, надежды нет...","form_frequencies":{"печаль":2,"повелителю":1,"тла":1,"джек":1,"видел":1,"признан":1,"даль":1,"земле":1,"светит":1,"душе":2,"сжёг":1,"хорош":1,"слава":1,"мире":1,"обращал":1,"корона":1,"груди":1,"престиж":1,"своём":1,"надежды":1,"круг":1,"детей":1,"прочих":1,"удивительно":1,"луной":1,"пройдёт":1,"тоской":1,"порочный":1,"ночи":1,"храбрецов":1,"народ":1,"ненавижу":1,"плеча":1,"зверей":1,"почёт":1,"жаль":1,"мир":1,"любого":1,"колотила":1,"жизнь":1,"бежать":1,"привык":1,"лучшим":1,"глупцов":1,"повелитель":1,"разорвать":1,"крыльях":1,"никто":1,"темноте":1,"ухнув":1,"головы":1,"конце":1,"чём":1,"чему":1,"памяти":1,"живущих":1,"ночную":1,"призрак":1,"гляжу":1,"тяжела":1,"шекспира":1,"тыкв":2,"оскала":1,"везде":1,"навожу":1,"мук":1,"самому":1,"немою":1,"