INSERT INTO public."SavedList" (id, language, title, content, "userId", "createdAt") VALUES ('cm54miren0003xvnx12dtuzvk', 'Russian', 'Буря - Пушкин', '{"inputText":"Ты видел деву на скале\nВ одежде белой над волнами\nКогда, бушуя в бурной мгле,\nИграло море с берегами,\nКогда луч молний озарял\nЕе всечасно блеском алым\nИ ветер бился и летал\nС ее летучим покрывалом?\nПрекрасно море в бурной мгле\nИ небо в блесках без лазури;\nНо верь мне: дева на скале\nПрекрасней волн, небес и бури.","form_frequencies":{"одежде":1,"прекрасно":1,"всечасно":1,"блесках":1,"видел":1,"бился":1,"мгле":2,"бушуя":1,"небо":1,"скале":2,"верь":1,"волн":1,"дева":1,"море":2,"летал":1,"луч":1,"покрывалом":1,"берегами":1,"озарял":1,"прекрасней":1,"бури":1,"молний":1,"играло":1,"ветер":1,"белой":1,"деву":1,"бурной":2,"блеском":1,"волнами":1,"небес":1,"алым":1,"летучим":1,"лазури":1},"entry_list":[{"frequency":2,"modelId":13942},{"frequency":2,"modelId":14916},{"frequency":2,"modelId":16277},{"frequency":2,"modelId":12869},{"frequency":2,"modelId":41405},{"frequency":2,"modelId":12868},{"frequency":2,"modelId":20074},{"frequency":2,"modelId":13172},{"frequency":1,"modelId":5},{"frequency":1,"modelId":12938},{"frequency":1,"modelId":59},{"frequency":1,"modelId":82},{"frequency":1,"modelId":38671},{"frequency":1,"modelId":182},{"frequency":1,"modelId":17505},{"frequency":1,"modelId":18016},{"frequency":1,"modelId":39079},{"frequency":1,"modelId":613},{"frequency":1,"modelId":688},{"frequency":1,"modelId":39187},{"frequency":1,"modelId":19787},{"frequency":1,"modelId":23509},{"frequency":1,"modelId":25930},{"frequency":1,"modelId":3595},{"frequency":1,"modelId":10666},{"frequency":1,"modelId":14100},{"frequency":1,"modelId":13488},{"frequency":1,"modelId":26003}]}', 'cm2qwe00n0000myhi8drf8l94', '2024-12-26 01:06:53.367');
INSERT INTO public."SavedList" (id, language, title, content, "userId", "createdAt") VALUES ('cm54mq7ta0005xvnxnk2utqnf', 'Russian', 'Её глаза - Пушкин', '{"inputText":"Она мила — скажу меж нами —\nПридворных витязей гроза,\nИ можно с южными звездами\nСравнить, особенно стихами,\nЕе черкесские глаза,\nОна владеет ими смело,\nОни горят огня живей;\nНо, сам признайся, то ли дело\nГлаза Олениной моей!\nКакой задумчивый в них гений,\nИ сколько детской простоты,\nИ сколько томных выражений,\nИ сколько неги и мечты!..\nПотупит их с улыбкой Леля —\nВ них скромных граций торжество;\nПоднимет — ангел Рафаэля\nТак созерцает божество.","form_frequencies":{"ангел":1,"меж":1,"огня":1,"дело":1,"южными":1,"поднимет":1,"рафаэля":1,"созерцает":1,"звездами":1,"стихами":1,"владеет":1,"потупит":1,"задумчивый":1,"олениной":1,"сравнить":1,"особенно":1,"томных":1,"улыбкой":1,"витязей":1,"мила":1,"горят":1,"скромных":1,"божество":1,"признайся":1,"черкесские":1,"выражений":1,"живей":1,"мечты":1,"торжество":1,"гений":1,"простоты":1,"гроза":1,"леля":1,"скажу":1,"граций":1,"детской":1,"смело":1,"сколько":3,"глаза":2,"придворных":1,"неги":1},"entry_list":[{"frequency":3,"modelId":73209},{"frequency":2,"modelId":12802},{"frequency":1,"modelId":73646},{"frequency":1,"modelId":13100},{"frequency":1,"modelId":13167},{"frequency":1,"modelId":13353},{"frequency":1,"modelId":13800},{"frequency":1,"modelId":72},{"frequency":1,"modelId":14412},{"frequency":1,"modelId":15081},{"frequency":1,"modelId":16299},{"frequency":1,"modelId":327},{"frequency":1,"modelId":18040},{"frequency":1,"modelId":526},{"frequency":1,"modelId":39156},{"frequency":1,"modelId":39189},{"frequency":1,"modelId":722},{"frequency":1,"modelId":20045},{"frequency":1,"modelId":939},{"frequency":1,"modelId":23231},{"frequency":1,"modelId":13929},{"frequency":1,"modelId":13945},{"frequency":1,"modelId":40704},{"frequency":1,"modelId":25283},{"frequency":1,"modelId":2518},{"frequency":1,"modelId":2593},{"frequency":1,"modelId":24230},{"fr