INSERT INTO public."SavedList" (id, language, title, content, "userId", "createdAt") VALUES ('cmc2f8pee0003tjrr1xvc7e1k', 'Russian', 'во мраке ночи', '{"inputText":"Я во мраке ночи всё метался в кошмаре\nСнилось мне, то рассыпалась как чешуя\nВсё тело в раз на куски\nИ взвыл я от тоски\nИбо этим распавшимся телом был я\n\nЯ был самым мистическим духом России Уау..\nЯ коварно был предан всей царской семьёй Уау..\nЯ мстил, как мстит сатана\nСпаслась только чудом одна\nБерегись, Распутин сочтётся с тобой\n\nВсё во мраке ночи связано с местью\nВсё во мраке ночи связано в месть\nПроклятье найдёт\nЭту девочку здесь\nВсё во мраке ночи\nЧует смерть\n\nНо постепенно ко мне возвращаются силы\nПовяжу я свой пояс и брызну духов\nЖдёт царевну страшный финал\nЯ скажу ей: \"Окончен бал!\"\nДо свиданья, Настя, твой час настал\n\nА во мраке ночи ужас родится\nЯ ей ещё покажу\nА во мраке ночи прячется зло Ууу..\nСмерть ей грозит\nВедь Распутин не спит\nА во мраке ночи\nСмерть страшней\n\nВсё во мраке ночи связано с местью (Зло в масть)\nВсё во мраке ночи ужаса рай (Мы - власть)\nУжаса рай, всё, конец и прощай\nВсё во мраке ночи\nВсё во мраке ночи\n\nЭй, вся нечисть, что мне подвластна\nК ней лети стрелой\nИ во мраке ночи\nИ во мраке ночи\n\nПусть покончат я этой несчастной\nИ во мраке ночи\nИ во мраке ночи\nИ во мраке ночи\nЕй конец","form_frequencies":{"местью":2,"возвращаются":1,"пояс":1,"скажу":1,"пусть":1,"грозит":1,"самым":1,"подвластна":1,"найдёт":1,"коварно":1,"масть":1,"связано":3,"чешуя":1,"финал":1,"куски":1,"покажу":1,"телом":1,"снилось":1,"чует":1,"страшней":1,"ибо":1,"чудом":1,"эй":1,"ночи":16,"сочтётся":1,"кошмаре":1,"бал":1,"россии":1,"метался":1,"берегись":1,"страшный":1,"свиданья":1,"месть":1,"девочку":1,"повяжу":1,"тело":1,"сатана":1,"царской":1,"мстил":1,"рассыпалась":1,"смерть":3,"проклятье":1,"прощай":1,"конец":2,"семьёй":1,"ууу":1,"рай":2,"ждёт":1,"спит":1,"зло":2,"спаслась":1,"распутин":2,"настя":1,"ко":1,"духов":1,"предан":1,"родится":1,"силы":1,"настал":1,"не